      if ( 0 == $('div.sf_ajax_loader').length )
      {
        $('body').append('
Chargement en cours...<'+'/div>');

        $(document).ajaxSend(function() {
          $('div.sf_ajax_loader').show();
        });

        $(document).ajaxStop(function() {
          setTimeout(function() { $('div.sf_ajax_loader').hide(); }, 250);
        });
      }
      else
      {
        setTimeout(function() { $('div.sf_ajax_loader').hide(); }, 250);
      }
